import React, { Component } from "react";
import "./dicegame.scss";


export default class DiceGame extends Component {
  state = {
    pointsToWin: 100,
    dices: [null, null],
    gameOver: false,
    players: [
      {
        playerTurn: true,
        totalScore: 0,
        tempScore: 0,
      },
      {
        playerTurn: false,
        totalScore: 0,
        tempScore: 0,
      },
    ]

  }

  rollDice() {
    return 1 + Math.floor(Math.random() * 6)
  }


