import React from 'react';
import DiceGame from '../DiceGame/DiceGame';
import TopPointsInput from '../../Components/TopPointsInput/TopPointsInput';
import Button from '../../Components/HoldAndDiceButtons/HoldAndDiceButtons';

export default class EnterGame extends React.Component {
  constructor() {
    super();
    this.state = {
      maxPoints: 100
    }
    this.saveToStorage = this.saveToStorage.bind(this);
  }
  saveToStorage() {
    this.setState((pervState) => {
      return pervState.playersTurn = 1 - pervState.playersTurn;
    });
    sessionStorage.setItem('maxPoints', this.state.maxPoints);
  }


  render() {
    return (
      <div>
        {this.saveToStorage()}
        <TopPointsInput classBack={this.saveToStorage} />
        <Button text="start" />
      </div>
    )
  }
}
